var st = require('st');
var http = require('http');

http.createServer(
  st(process.cwd())
).listen(1337);

var os = require('os');
var ifaces = os.networkInterfaces();

var myip = '';

for(var key in ifaces){
  var iface = ifaces[key];
  iface.forEach(function(details){
    if(details.family === 'IPv4' && !details.internal){
      myip = details.address;
    }
  });
}
